function indefinite_article(next_word_type, caps = false) {
      if (next_word_type === "nouns") {
        var next_word = nouns[Math.floor(Math.random()*nouns.length)]
      }
      if(next_word_type === "adjectives") {
        var next_word = adjectives[Math.floor(Math.random()*adjectives.length)]
      }
      if(next_word_type === "present_verbs") {
        var next_word = present_verbs[Math.floor(Math.random()*present_verbs.length)]
      }

      var vowelRegex = '^[aieouAIEOU].*'
      var matched = next_word.match(vowelRegex)

      if(matched)
      {
       return caps ? 'An ' + next_word : "an " + next_word
      }
      else
      {
       return caps ? 'A ' + next_word : "a " + next_word
      }
}

function randomWord(word_type) {
  if (word_type === "nouns") {
    var next_word = nouns[Math.floor(Math.random()*nouns.length)]
  }
  if(word_type === "adjectives") {
    var next_word = adjectives[Math.floor(Math.random()*adjectives.length)]
  }
  if(word_type === "present_verbs") {
    var next_word = present_verbs[Math.floor(Math.random()*present_verbs.length)]
  }
  if(word_type === "plural_verbs") {
    var next_word = plural_verbs[Math.floor(Math.random()*plural_verbs.length)]
  }
  if(word_type === "singular_verbs") {
    var next_word = singular_verbs[Math.floor(Math.random()*singular_verbs.length)]
  }

  return next_word;

}



